const showCertificates=()=>{const allImg=[...document.querySelectorAll(".certificates img")],show=document.querySelector(".show"),showImg=document.createElement("img");show.appendChild(showImg),allImg.forEach(img=>{img.addEventListener("click",()=>{show.classList.add("active");const imgSrc=img.getAttribute("src"),imgAlt=img.getAttribute("alt");showImg.setAttribute("src",imgSrc),showImg.setAttribute("alt",imgAlt)})}),show.addEventListener("click",()=>{show.classList.remove("active")})};showCertificates();