const navigation=()=>{const burger=document.querySelector(".burger"),navLinks=document.querySelector(".nav-links"),navLinksLi=document.querySelectorAll(".nav-links li"),nav=document.querySelector("nav"),header=document.querySelector("header");let sticky=nav.offsetTop;burger.addEventListener("click",(function(){burger.classList.toggle("active"),navLinks.classList.toggle("nav-active"),navLinksLi.forEach((link,index)=>{link.style.animation?link.style.animation="":link.style.animation=`navLinkFade 0.5s ease forwards ${index/5+.3}s`})})),window.addEventListener("scroll",()=>{burger.classList.remove("active"),navLinks.classList.remove("nav-active"),navLinksLi.forEach(link=>{link.style.animation=""}),window.pageYOffset>sticky?(header.style.paddingTop="100px",nav.classList.add("sticky")):(header.style.paddingTop="0",nav.classList.remove("sticky"))})},currentDate=()=>{const currentYear=new Date,span=document.querySelector(".date");span.innerText=currentYear.getFullYear()},app=()=>{navigation(),currentDate()};app();